import React, { Component } from "react";
import CourseDataService from "../service/CourseDataService";

const INSTRUCTOR = "in28minutes";

class ListBooksComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      currentPage: 1,
      todosPerPage: 3,
      message: null
    };
    this.deleteBookClicked = this.deleteBookClicked.bind(this);
    this.updateBookClicked = this.updateBookClicked.bind(this);
    this.addBookClicked = this.addBookClicked.bind(this);
    this.refreshbooks = this.refreshBooks.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    this.refreshBooks();
  }

  handleClick(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }

  refreshBooks() {
    CourseDataService.retrieveAllCourses(INSTRUCTOR) //HARDCODED
      .then(response => {
        console.log(response);
        this.setState({ books: response.data });
      });
  }

  deleteBookClicked(id) {
    CourseDataService.deleteCourse(INSTRUCTOR, id).then(response => {
      this.setState({ message: `Delete of course ${id} Successful` });
      this.refreshBooks();
    });
  }

  addBookClicked() {
    this.props.history.push(`/courses/-1`);
  }

  updateBookClicked(id) {
    console.log("update " + id);
    console.log("This.props.hh", this.state.books);
    this.props.history.push(`/courses/${id}`);
  }

  render() {
    const { books, currentPage, todosPerPage } = this.state;

    // Logic for displaying current todos
    const indexOfLastTodo = currentPage * todosPerPage;
    const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
    const currentTodos = books.slice(indexOfFirstTodo, indexOfLastTodo);
    console.log(currentTodos)

/*     const renderTodos = currentTodos.map((todo, index) => {
      return <li key={index}>{todo}</li>;
    }); */

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(books.length / todosPerPage); i++) {
      pageNumbers.push(i);
    }

    const renderPageNumbers = pageNumbers.map(number => {
      return (
        <li key={number} id={number} onClick={this.handleClick}>
          {number}
        </li>
      );
    });

    console.log("render");
    return (
      <div className="container">
        <h3>Books</h3>
        {this.state.message && (
          <div class="alert alert-success">{this.state.message}</div>
        )}
        <div className="container">
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>Title</th>
                <th>Author</th>
                <th>ISBN-13</th>
                <th>Comments</th>
                <th>Update</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {currentTodos.map(course => (
                <tr key={course.id}>
                  <td>{course.id}</td>
                  <td>{course.description}</td>
                  <td>{course.author}</td>
                  <td>{course.isbn}</td>
                  <td>{course.commentCount}</td>
                  <td>
                    <button
                      className="btn btn-success"
                      onClick={() => this.updateBookClicked(course.id)}
                    >
                      Update
                    </button>
                  </td>
                  <td>
                    <button
                      className="btn btn-warning"
                      onClick={() => this.deleteCourseClicked(course.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="row">
            <button className="btn btn-success" onClick={this.addCourseClicked}>
              Add book
            </button>
          </div>
          <div>
            <ul id="page-numbers">{renderPageNumbers}</ul>
          </div>
        </div>
      </div>
    );
  }
}

export default ListBooksComponent;
