import axios from 'axios'

const ROOT_API_URL = 'http://localhost:8080'

class BooksDataService {

    retrieveAllBooks(name) {
        //console.log('executed service')
        return axios.get(`${ROOT_API_URL}/students`);
    }

    retrieveBook(name, id) {
        //console.log('executed service')
        return axios.get(`${ROOT_API_URL}/students/${id}`);
    }

    deleteBook(name, id) {
        //console.log('executed service')
        return axios.delete(`${ROOT_API_URL}/students/${id}`);
    }

    updateBook(name, id, book) {
        //console.log('executed service')
        return axios.put(`${ROOT_API_URL}/students/${id}`, book);
    }

    createBook(name, book) {
        //console.log('executed service')
        return axios.post(`${ROOT_API_URL}/students/`, book);
    }
}

export default new BooksDataService()
