import React, { Component } from "react";
import BooksDataService from "../service/BooksDataService";
import IconButton from '@material-ui/core/IconButton';
import DeleteIcon from '@material-ui/icons/Delete';

class ListBooksComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      currentPage: 1,
      booksPerPage: 3,
      message: null
    };
    this.deleteBookClicked = this.deleteBookClicked.bind(this);
    this.updateBookClicked = this.updateBookClicked.bind(this);
    this.addBookClicked = this.addBookClicked.bind(this);
    this.refreshbooks = this.refreshBooks.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    this.refreshBooks();
  }

  handleClick(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }

  refreshBooks() {
    BooksDataService.retrieveAllBooks() //HARDCODED
      .then(response => {
        console.log(response);
        this.setState({ books: response.data });
      });
  }

  deleteBookClicked(id) {
    BooksDataService.deleteBook(id).then(response => {
      this.setState({ message: `Delete of course ${id} Successful` });
      this.refreshBooks();
    });
  }

  addBookClicked() {
    console.log(this.props.history)
    this.props.history.push(`/books/-1`);
  }

  updateBookClicked(id) {
    console.log("update " + id);
    console.log("This.props.hh", this.state.books);
    this.props.history.push(`/books/${id}`);
  }

  render() {
    const { books, currentPage, booksPerPage } = this.state;

    // Logic for displaying current todos
    const indexOfLastTodo = currentPage * booksPerPage;
    const indexOfFirstTodo = indexOfLastTodo - booksPerPage;
    const currentBooks = books.slice(indexOfFirstTodo, indexOfLastTodo);

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(books.length / booksPerPage); i++) {
      pageNumbers.push(i);
    }

    const renderPageNumbers = pageNumbers.map(number => {
      return (
        <li key={number} id={number} onClick={this.handleClick}>
          {number}
        </li>
      );
    });

    console.log("render");
    return (
      <div className="container">
        <h3>Books</h3>
        {this.state.message && (
          <div class="alert alert-success">{this.state.message}</div>
        )}
        <div className="container">
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>Title</th>
                <th>Author</th>
                <th>ISBN-13</th>
                <th>Comments</th>
                <th>Update</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {currentBooks.map(book => (
                <tr key={book.id}>
                  <td>{book.id}</td>
                  <td>{book.description}</td>
                  <td>{book.author}</td>
                  <td>{book.isbn}</td>
                  <td>{book.commentCount}</td>
                  <td>
                    <button
                      className="btn btn-success"
                      onClick={() => this.updateBookClicked(book.id)}
                    >
                      Update
                    </button>
                  </td>
                  <td>
                    <button
                      className="btn btn-warning"
                      onClick={() => this.deleteBookClicked(book.id)}
                    >
                      Delete
                    </button>
                    <IconButton aria-label="Delete">
            <DeleteIcon
              onClick={() => this.deleteBookClicked(book.id)}
            />
          </IconButton>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div>
            <button className="btn btn-success btn-add float-right" onClick={this.addBookClicked}>
              Add book
            </button>
          </div>
          <div>
            <ul className="pagination" id="page-numbers">{renderPageNumbers}</ul>
          </div>
        </div>
      </div>
    );
  }
}

export default ListBooksComponent;
