import axios from 'axios'

const INSTRUCTOR = 'in28minutes'
const COURSE_API_URL = 'http://localhost:8080'
const BOOKS_API_URL = `${COURSE_API_URL}/instructors/${INSTRUCTOR}`

class BooksDataService {

    retrieveAllBooks(name) {
        //console.log('executed service')
        return axios.get(`${BOOKS_API_URL}/books`);
    }

    retrieveBook(name, id) {
        //console.log('executed service')
        return axios.get(`${BOOKS_API_URL}/books/${id}`);
    }

    deleteBook(name, id) {
        //console.log('executed service')
        return axios.delete(`${BOOKS_API_URL}/books/${id}`);
    }

    updateBook(name, id, course) {
        //console.log('executed service')
        return axios.put(`${BOOKS_API_URL}/books/${id}`, course);
    }

    createBook(name, course) {
        //console.log('executed service')
        return axios.post(`${BOOKS_API_URL}/books/`, course);
    }
}

export default new BooksDataService()
